package com.jumio.gui;

import android.content.res.Resources;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.graphics.BitmapFactory.Options;
import android.util.Base64;
import java.io.ByteArrayInputStream;

public class Images {
    public static final String POWERED_BY_JUMIO_WHITE_RESOURCE = "";

    public static Bitmap getImage(Resources resources) {
        Options options = new Options();
        options.inDensity = 320;
        options.inTargetDensity = resources.getDisplayMetrics().densityDpi;
        return BitmapFactory.decodeStream(new ByteArrayInputStream(Base64.decode(POWERED_BY_JUMIO_WHITE_RESOURCE, 0)), null, options);
    }
}
